mapper.select:
		select `id`,`name`,`order_num`,`type`,`create_time` from web_ajb_type
        
        	 and is_deleted = 0  
  		   and id = #{id} 
  		   and name like '%${name}%' 
  		   and type = #{type} 
  		   and (type like '%${searchName}%' or name like '%${searchName}%') 
  		
        
            
                order by ${sort} ${order}
            
			
                order by type desc,order_num asc
			
        
		
			limit #{offset}, #{limit}

		select count(*) from web_ajb_type
		  
		 	and is_deleted = 0   
  		   and id = #{id} 
  		   and name like '%${name}%' 
  		   and type = #{type} 
  		   and (type like '%${searchName}%' or name like '%${searchName}%')
