mapper.select:
		select `id`,`title`,`fk_type_id1`,`fk_type_id2`,`context`,`create_time`,`create_user`,`modify_time`,`is_deleted` from web_school_education_info

         and is_deleted = 0
		  		   and id = #{id} 
		  		   and title = #{title} 
		  		   and fk_type_id1 = #{fkTypeId1} 
		  		   and fk_type_id2 = #{fkTypeId2} 
		  		   and context = #{context} 
		  		   and create_time = #{createTime} 
		  		   and create_user = #{createUser} 
		  		   and modify_time = #{modifyTime} 
		  		   and is_deleted = #{isDeleted} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from web_school_education_info

  and is_deleted = 0
  		   and id = #{id} 
