mapper.select:
		select wsei.id,wsei.title,wsei.fk_type_id1,wsei.fk_type_id2,(select wse1.type type from web_school_education wse1 where wse1.id = wsei.fk_type_id1 and wse1.is_deleted = 0) fkType1,(select wse2.type type from web_school_education wse2 where wse2.id = wsei.fk_type_id2 and wse2.is_deleted = 0) fkType2,wsei.context,wsei.create_time,wsei.create_user,wsei.modify_time,wsei.is_deleted from web_school_education_info wsei

         and is_deleted = 0
		  		   and wsei.id = #{id} 
		  		   and wsei.title = #{title} 
		  		   and wsei.fk_type_id1 = #{fkTypeId1} 
		  		   and fk_type_id2 = #{fkTypeId2} 
		  		   and wsei.context = #{context} 
		  		   and wsei.create_time = #{createTime} 
		  		   and wsei.create_user = #{createUser} 
		  		   and wsei.modify_time = #{modifyTime} 
		  		   and wsei.is_deleted = #{isDeleted} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from web_school_education_info wsei

  and wsei.is_deleted = 0
  		   and wsei.id = #{id} 
  		   and wsei.title = #{title} 
  		   and wsei.fk_type_id1 = #{fkTypeId1} 
  		   and wsei.fk_type_id2 = #{fkTypeId2} 
  		   and wsei.context = #{context} 
  		   and wsei.create_time = #{createTime} 
  		   and wsei.create_user = #{createUser} 
  		   and wsei.modify_time = #{modifyTime} 
  		   and wsei.is_deleted = #{isDeleted}

mapper.update:
update web_school_education_info
modify_time = NOW(), title = #{title}, fk_type_id1 = #{fkTypeId1}, fk_type_id2 = #{fkTypeId2}, context = #{context}, create_time = #{createTime}, create_user = #{createUser}, is_deleted = #{isDeleted}
where id = #{id}
