mapper.select:
		select `id`,`title`,`wheel_url`,`img_url`,`is_deleted` from web_wheel

        	 and is_deleted = 0 
		  		   and id = #{id} 
		  		   and title = #{title} 
		  		   and wheel_url = #{wheelUrl} 
		  		   and img_url = #{imgUrl} 
		  		   and is_deleted = #{isDeleted} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from web_wheel

 	 and is_deleted = 0 
  		   and id = #{id} 
  		   and title = #{title} 
  		   and wheel_url = #{wheelUrl} 
  		   and img_url = #{imgUrl} 
